import javafx.application.Application;import javafx.fxml.FXMLLoader;import javafx.scene.Parent;import javafx.scene.Scene;import javafx.stage.Stage;/** * Created by Admin on 15.11.15. */public class Main extends Application {  public static void main(String[] args) {    launch(args);  }  @Override  public void start(Stage stage) throws Exception {    Parent loader = FXMLLoader.load(getClass().getResource("Main.fxml"));    Scene scene = new Scene(loader);    stage.setTitle("Image segmentation");    stage.setScene(scene);    stage.show();  }}